import React, { useState, useEffect } from "react";
import axios from "axios";
import { useHistory } from "react-router";
import { connect } from "react-redux";
import { addProject } from "../reducer/actions";
import { useParams } from "react-router-dom";

function EditProject(props) {
  const { projects } = props;

  const { push } = useHistory();
  const params = useParams();

  //   const [project, setProject] = useState({
  //     project_name: "",
  //     project_leader: "",
  //   });
  const [project, setProject] = useState("");
  useEffect(() => {
    axios.get(`http://localhost:4000/api/projects`).then((res) => {
      //   res.data.filter((pr) => {
      //     console.log(pr.project_id == params.id);
      //   });

      res.data.map((pr) => {
        if ((pr.project_id = params.id)) {
          console.log(pr);
          setProject({
            ...project,
            [project.project_name]: pr.project_name,
            [project.project_leader]: pr.project_leader,
          });
        }
      });
    });
  }, []);
  console.log(project);

  const handleChange = (e) => {
    setProject({ ...project, [e.target.name]: e.target.value });
  };
  const submit = (e) => {
    e.preventDefault();
  };
  return (
    <div>
      <div>
        <form onSubmit={submit}>
          <h2>Project</h2>
          <label>ProjectName :</label>
          <input
            name="project_name"
            type="text"
            value={project.project_name}
            onChange={handleChange}
          />
          <label>ProjectLeader :</label>
          <input
            name="project_leader"
            type="text"
            value={project.project_leader}
            onChange={handleChange}
          />
          <button>save!</button>
        </form>
        {/* <p>{error}</p> */}
      </div>
    </div>
  );
}
const mapStateToProps = (state) => {
  return {
    projects: state.projects,
  };
};
export default connect(mapStateToProps)(EditProject);
