import React, { useState } from "react";
import { connect } from "react-redux";
import { addTask } from "../reducer/actions";
import { useParams } from "react-router-dom";
import { useHistory } from "react-router";
import styled from "styled-components";

function AddTask(props) {
  const [error, setError] = useState("");
  const { dispatch } = props;
  const params = useParams();

  const { push } = useHistory();

  const [task, setTask] = useState({
    task_name: "",
    task_information: "",
  });

  const handleChange = (e) => {
    setTask({ ...task, [e.target.name]: e.target.value });
  };

  const submit = (e) => {
    e.preventDefault();
    if (task.task_name === "" || task.task_information === "") {
      setError("ALL FIELDS REQUIRED!");
    } else {
      dispatch(addTask(task, params.project_id));
      push(`/singleProject/${params.project_id}`);
    }
  };

  return (
    <MainContainer>
      <Form onSubmit={submit}>
        <h2>Add Task</h2>
        <label>Task :</label>
        <input
          name="task_name"
          type="text"
          placeholder="Title"
          onChange={handleChange}
          value={task.task_name}
        />
        <label>Info :</label>
        <input
          name="task_information"
          type="text"
          placeholder="Text"
          onChange={handleChange}
          value={task.task_information}
        />
        <Button>
          <button>Add Task!</button>
          <p>{error}</p>
        </Button>
      </Form>
    </MainContainer>
  );
}

export default connect()(AddTask);

const MainContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;

  h2 {
    font-family: fira sans;
    color: #ffffff;
    font-weight: 30;
    font-size: 1.8rem;
  }
`;
const Form = styled.form`
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
  background-color: #bfc7d2;
  padding: 20px;
  width: 90%;
  height: 30vh;
  margin: auto;
  box-shadow: 30px 30px 2px black, 15px 15px 1px 2px rgba(0, 0, 0, 0);
  .category {
    display: flex;
    flex-direction: column;
    justify-content: space-evenly;
    height: 30%;
    display: flex;
    flex-direction: column;

    label {
      font-family: fira sans;
      font-size: 1.2rem;
    }

    input {
      padding: 10px;
      border-radius: 7px;
      :hover {
        border: 2px solid #257dae;
        box-shadow: 0px 0px 1px 1px #257dae;
        transition-duration: 0.2s;
      }
    }
  }
`;

const Button = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-evenly;
  align-items: center;
  margin-top: 30px;
  width: 100%;
  button {
    display: flex;
    flex-direction: column;
    justify-content: space-evenly;
    align-items: center;
    width: 30%;
    height: 7vh;
    background-color: #257dae;
    padding: 0;
    border-radius: 5px;
    border: 1px solid transparent;
    :hover {
      box-shadow: 0px 0px 3px 3px grey;
      transition-duration: 0.2s;
    }
    h4 {
      display: flex;
      flex-direction: row;
      justify-content: space-evenly;
      align-items: center;
      width: 60%;
      color: #ffffff;
      font-weight: 30;
      font-size: 1.2rem;
    }
  }

  p {
    color: red;
    font-weight: bold;
    font-family: fira sans;
  }
`;
