import React, { useEffect } from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import styled from "styled-components";
import { getProjects } from "../reducer/actions";
import AddCircleOutlineIcon from "@mui/icons-material/AddCircleOutline";

function ProjectListings(props) {
  const { projects, dispatch } = props;
  useEffect(() => {
    dispatch(getProjects());
  }, []);

  return (
    <ListingContainer>
      <Bar>
        <h2>Projects:</h2>
        <div className="button">
          <Link className="link" to="addproject">
            <p>
              Add Project <AddCircleOutlineIcon />
            </p>
          </Link>
        </div>
      </Bar>

      {projects.map((pr) => {
        return (
          <ProjectContainer>
            <Link className="link" to={`/singleProject/${pr.project_id}`}>
              <ProjectSection>
                <h3>Project:</h3>
                <h4>{pr.project_name}</h4>
              </ProjectSection>
              <ProjectSection>
                <h3>Project-Leader:</h3>
                <h4>{pr.project_leader}</h4>
              </ProjectSection>
            </Link>
          </ProjectContainer>
        );
      })}
    </ListingContainer>
  );
}
const mapStateToProps = (state) => {
  return {
    projects: state.projects,
  };
};
export default connect(mapStateToProps)(ProjectListings);

const ListingContainer = styled.div`
  font-family: Oxygen;
  display: flex;
  width: 90%;
  margin: auto;
  flex-direction: column;
  justify-content: space-evenly;
  .link {
    text-decoration: none;
    color: black;
  }
`;

const Bar = styled.div`
display: flex;
flex-direction: row;
justify-content space-between;
align-content: center;
width: 100%;


h2{
font-family: fira sans;
font-weight: 20;
font-size: 2rem;
color: #FFFFFF

}

.button {
  display: flex;
  flex-direction; column;
  justify-content: space-evenly;
  align-items: center;
  width: 20%;
  .link{
   display: flex;
   flex-direction: column;
   justify-content: space-evenly;
   align-items: center;
    text-decoration: none;
    border: 1px solid #257DAE;
    background-color: #257DAE;
 width: 100%;
 height: 8vh;

 border: 1px solid transparent;
  border-radius: 3px;
  box-shadow: rgba(255, 255, 255, .4) 0 1px 0 0 inset;
  box-sizing: border-box;

p{
  display: flex;
  flex-direction: row;
  justify-content: space-evenly;
  align-items: center;
  width: 100%;
  color: #FFFFFF;
font-size: 1.2rem;
}
  }
}

`;

const ProjectContainer = styled.div`
  display: flex;
  width: 100%;
  flex-direction: row;
  justify-content: space-evenly;
  border: 1px solid black;
  background-color: #d9d9d9;
  margin-bottom: 20px;
  :hover {
    background-color: grey;
  }
`;
const ProjectSection = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-between;

  h3 {
    font-size: 1.3rem;
    padding: 15px;
    margin: 0;
    width: 50%;
  }
  h4 {
    font-size: 1.2rem;
    padding: 5px;
  }
`;
