import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import { deleteProject, deleteTask } from "../reducer/actions";
import { useHistory } from "react-router";
import styled from "styled-components";
import AddCircleOutlineIcon from "@mui/icons-material/AddCircleOutline";
import CheckCircleOutlineIcon from "@mui/icons-material/CheckCircleOutline";
import EditIcon from "@mui/icons-material/Edit";

function SingleProject(props) {
  const [result, setResult] = useState([]);
  const [noTasks, SetNoTasks] = useState(false);
  const { projects, dispatch, location } = props;
  const params = useParams();
  const { push } = useHistory();

  useEffect(() => {
    axios
      .get(`https://mojoplanner.herokuapp.com/api/projects/${params.id}`)
      .then((res) => {
        console.log(res.data);
        setResult(res.data);
      });
  }, []);

  const deletePR = (e) => {
    e.preventDefault();
    dispatch(deleteProject(params.id));
    push("/projectListings");
  };
  const deleteTsk = (taskID) => {
    dispatch(deleteTask(taskID));
    push(`/singleProject/${params.id}`);
    //window.location.reload();
  };

  return (
    <div>
      {result.map((pr) => {
        {
          if (pr.project_tasks.length === 0) {
            setResult(true);
          } else {
            setResult(false);
          }
        }
        return (
          //<MainContainer>
          <MainContainer key={pr.project_id}>
            <div>
              <ProjectName>
                <div className="names">
                  <h3>{pr.project_name}</h3>
                  <h3>{pr.project_leader}</h3>
                </div>

                <div className="addTask">
                  <Link className="button" to={`/addTask/${pr.project_id}`}>
                    <p>
                      Add Task <AddCircleOutlineIcon />
                    </p>
                  </Link>
                </div>
              </ProjectName>
              {pr.project_tasks.map((tsk) => {
                return (
                  <Information key={tsk.task_id}>
                    <div className="eachTask">
                      <div className="text">
                        <h5>Title:</h5>
                        <h4>{tsk.task_name}</h4>
                      </div>
                      <div className="text">
                        <h5>Task:</h5>
                        <h4 className="taskInfo">{tsk.task_information}</h4>
                      </div>
                    </div>
                    <div className="taskButtons">
                      <Link
                        className="editTask"
                        to={`/editTask/${pr.project_id}/${tsk.task_id}`}
                      >
                        <p>
                          Edit Task <EditIcon />
                        </p>
                      </Link>
                      <button
                        className="deleteTask"
                        onClick={() => deleteTsk(tsk.task_id)}
                      >
                        <p>
                          Task Finished <CheckCircleOutlineIcon />
                        </p>
                      </button>
                    </div>
                  </Information>
                );
              })}
            </div>
          </MainContainer>
        );
      })}
      <EndButtons>
        <Link className="editProject" to={`/editProject/${params.id}`}>
          <p>
            Edit Project <EditIcon />
          </p>
        </Link>

        <button className="deleteProject" onClick={deletePR}>
          <p>
            Delete Project <CheckCircleOutlineIcon />
          </p>
        </button>
      </EndButtons>
    </div>
  );
}

const mapStateToProps = (state) => {
  return {
    projects: state.projects,
  };
};

export default connect(mapStateToProps)(SingleProject);

const MainContainer = styled.div`
  display: flex;
  flex-direction: column;
  //justify-content: space-between;
  width: 90%;
  margin: auto;
  // height: 85vh;
  margin-bottom: 100px;
`;

const ProjectName = styled.div`
display: flex;
flex-direction: row;
width: 100%
justify-content: space-between;
align-items: center;
.names {
  display: flex;
  flex-direction: row;
  justify-content: space-evenly;
  width: 70%;
  h3{
    font-weight: 40;
    font-family: fira sans;
    color: #FFFFFF;
    font-size: 1.4rem;

  }
}
.addTask{
  width: 30%;
  display: flex;
  flex-direction: row;
  justify-content: flex-end;
  .button{
    text-decoration: none;
    display: flex;
    flex-direction: column;
    justify-content: space-evenly;
    align-items: center;
     text-decoration: none;
     border: 1px solid #257DAE;
     background-color: #257DAE;
  width: 50%;
  height: 5vh;

  border: 1px solid transparent;
   border-radius: 3px;
   box-shadow: rgba(255, 255, 255, .4) 0 1px 0 0 inset;
   box-sizing: border-box;
   :hover {
    box-shadow: 0px 0px 3px 3px grey;
    transition-duration: 0.2s;
  }
   p{
    display: flex;
    flex-direction: row;
    justify-content: space-evenly;
    align-items: center;
    width: 80%;
     font-family: fira sans;
     color: #FFFFFF

   }
  }
}
`;

const Information = styled.div`
  display: flex;
  flex-direction: row;
  background-color: #d9d9d9;
  margin-top: 20px;
  .eachTask {
    display: flex;
    flex-direction: column;
    width: 70%;
    padding: 20px;
    .text {
      margin-left: 20px;
      display: flex;
      flex-direciton: row;
      justify-content: space-between;
      margin-bottom: 10px;
      h5,
      h4 {
        font-family: fira sans;
        color: #black;
        font-weight: 50;
        font-size: 1.1rem;
        margin: 0;
      }
      h4 {
        width: 90%;
        background-color: #ffffff;
        padding: 10px;
        border: 1px solid #e46363;
      }
      h5 {
        font-weight: bold;
      }
      .taskInfo {
        height: 15vh;
        width: 90%;
        overflow-wrap: break-word;
      }
    }
  }

  .taskButtons {
    width: 30%;
    display: flex;
    flex-direction: column;
    justify-content: space-evenly;
    align-items: center;
    text-align: center;

    .editTask,
    .deleteTask {
      display: flex;
      justify-content: space-evenly;
      align-items: center;
      text-decoration: none;
      width: 70%;
      height: 7vh;
      border: 1px solid transparent;
      border-radius: 10px 10px 10px;
      :hover {
        box-shadow: 0px 0px 3px 3px grey;
        transition-duration: 0.2s;
      }
      p {
        display: flex;
        flex-direction: row;
        justify-content: space-evenly;
        align-items: center;
        width: 60%;
        color: #ffffff;
        font-family: fira sans;
        font-size: 1.1rem;
      }
    }
    .editTask {
      background-color: #0b343d;
    }
    .deleteTask {
      background-color: #e46363;
    }
  }
`;

const EndButtons = styled.div`
  width: 100%;
  padding: 20px;
  position: fixed;
  bottom: 0%;
  opacity: 1;
  background-color: #244f58;

  display: flex;
  flex-direction: row;
  justify-content: space-evenly;
  text-align: center;
  .editProject,
  .deleteProject {
    width: 20%;
    padding: 5px;
    border: 1px solid transparent;
    border-radius: 3px;
    :hover {
      box-shadow: 0px 0px 3px 3px grey;
      transition-duration: 0.2s;
    }
    p {
      margin: 0;
      color: #ffffff;
      font-family: fira sans;
      font-weight: bold;
      font-size: 1.3rem;
      display: flex;
      flex-direction: row;
      justify-content: space-evenly;
      align-items: center;
      width: 70%;
      margin: auto;
    }
  }
  .editProject {
    background-color: #0b343d;
    text-decoration: none;
  }
  .deleteProject {
    background-color: #e46363;
  }
`;
